'use strict';


/**
* File Banner / Header - Utitity function used by Gulp-Tasks
* @return { String }
*/
function getBannerMessage(templateType, msgPackageName, includeTimeStamp) {

  msgPackageName = msgPackageName || 'gulp';
  includeTimeStamp = includeTimeStamp || false;
  templateType = templateType || 'TPL_SLASH';

  let buildDate = new Date().toGMTString();

  // File Header/Banner
  //--------------
  const TEMPLATE_SLASH = ['//-----------------------------------',
    '//\n//\n//\n//\n//',
    '// - THIS IS AN AUTOGENERATED FILE. DO NOT EDIT THIS FILE DIRECTLY -',
    '// - Generated by Gulp (' + msgPackageName + ').',
    (includeTimeStamp) ? '//\n// - @build: ' + buildDate : '//',
    '//\n//\n//\n//\n//',
    '//-----------------------------------',
    '\n\n'].join('\n');


  const TEMPLATE_ASTERISK = ['/**',
    ' *\n *\n *',
    ' * - THIS IS AN AUTOGENERATED FILE. DO NOT EDIT THIS FILE DIRECTLY -',
    ' * - Generated by Gulp (' + msgPackageName + ').',
    (includeTimeStamp) ? '*\n* - @build: ' + buildDate : ' *',
    ' *\n *\n *',
    ' */\n\n',
    ''].join('\n');


  // Return Text Template
  return (templateType === 'TPL_ASTERISK')
    ? TEMPLATE_ASTERISK
    : TEMPLATE_SLASH;

}

module.exports.getBannerMessage = getBannerMessage;
